(url.indexOf('24x7loans.com')==-1) && 
(url.indexOf('meridian')==-1) && 
(url.indexOf('66.237.113.179')==-1) && 
(url.indexOf('void')==-1) && 
(url.length > 0)){
if(confirm(confirm_msg)){
window.open(url);
}
}
else{
window.open(url);
}
}
