function signup_complete(){
var id, label, fields=[ 'name', 'email', 'phone', 'extra' ];
for(var i=0; i<fields.length; i++){
id=fields[i];
label=id+'_label';
if( $(id) && ($(label).innerHTML.indexOf('*') > -1) && ($F(id)=='') ){
